import React, { useState } from "react";

function App() {
  const [country, setCountry] = useState("");
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);

  const fetchCovidData = async () => {
    setLoading(true);
    setData(null);
    let url = "http://localhost:8000/covid";
    if (country) {
      url += `/${country}`;
    }
    try {
      const response = await fetch(url);
      const json = await response.json();
      setData(json);
    } catch (error) {
      setData({ error: "Error fetching data" });
    }
    setLoading(false);
  };

  return (
    <div style={{ maxWidth: 400, margin: "40px auto", fontFamily: "Arial" }}>
      <h2>Dashboard COVID-19</h2>
      <input
        type="text"
        placeholder="Enter the country (e.g., Canada)"
        value={country}
        onChange={e => setCountry(e.target.value)}
        style={{ width: "70%", padding: "8px" }}
      />
      <button onClick={fetchCovidData} style={{ padding: "8px 16px", marginLeft: 8 }}>
        Search
      </button>
{loading && (
        <div style={{
          position: "fixed",
          top: 0, left: 0, right: 0, bottom: 0,
          background: "rgba(0,0,0,0.3)",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          zIndex: 9999
        }}>
          <div style={{
            background: "#fff",
            padding: "32px 48px",
            borderRadius: 8,
            boxShadow: "0 2px 8px rgba(0,0,0,0.2)",
            fontSize: 20
          }}>
            Loading...
          </div>
        </div>
      )}
      {data && (
        <pre style={{ background: "#f4f4f4", padding: 16, marginTop: 16 }}>
          {JSON.stringify(data, null, 2)}
        </pre>
      )}
    </div>
  );
}

export default App;
